INSERT INTO `features_event` (`key`, `value`)
VALUES
	('event_seminaries_desc','Wisembly gives everyone at your seminar the chance to speak.  Collect all the questions and provide a better response!'),
	('event_seminaries_title','Get your audience to join in'),
	('event_stream_item_1_author','Sébastien F. via email'),
	('event_stream_item_1_text','Should we change the top management to become digital ?'),
	('event_stream_item_2_author','Cédric E. via mobile'),
	('event_stream_item_2_text','Being Digital Ready for our businesses is it not a matter of survival?'),
	('event_stream_item_3_author','Cécile D. via Web'),
	('event_stream_item_3_text','What place for innovation in companies run by 1.0 profiles?'),
	('event_stream_item_4_author','Mohamed M. via Twitter'),
	('event_stream_item_4_text','Finally a true reflection on a business that could change .....Change management is hard .... bravo!'),
	('event_stream_item_5_author','Christina D. via SMS'),
	('event_stream_item_5_text','Thank you for answering all our questions, that\'s great!'),
	('feedback_survey_desc','<p>Wisembly has an integrated feedback survey so you can collect feedback at any time, directly via our platform.  Unlike paper questionnaires or emails sent after the event, Wisembly surveys guarantee a maximum response rate.p> <p>Collect feedback at each event and monitor progress on Wisembly to better adapt content to audience expectations. </p>'),
	('feedback_survey_link','Feedback survey'),
	('feedback_survey_title','Make your events better and better'),
	('fullscreen_desc','Display messages from the audience in the room to boost interaction whilst highlighting audience reactions.  You can toggle between slides and questions, choose the display method, and customize the colors and logo of your screen.'),
	('fullscreen_link','Full screen'),
	('fullscreen_title','Display a 100% interactive screen'),
	('interaction_analysis_desc1','Wisembly collates and analyzes all your data after each event: questions, comments, votes, number of participants, word cloud, etc.'),
	('interaction_analysis_desc2','You can visualize the highlights of your event from the GUI and export them in different formats.'),
	('interaction_analysis_link','Interaction analysis'),
	('interaction_analysis_title','Visualize the content of your event'),
	('moderation_desc','You can moderate messages from the audience before publication, if you wish, then only messages that you approve will be published.  You can also moderate messages after publication, if you change your mind.'),
	('moderation_link','Moderation'),
	('moderation_title','Moderate messages from the audience'),
	('multi_devices_desc1','Why change the habits of your audience by making them use cumbersome vote boxes or keypads?'),
	('multi_devices_desc2','<p>Web, cellphone, tablet, Twitter, email, SMS, etc.</p> <p>Wisembly works with all modern devices.</p>'),
	('multi_devices_desc3','Participants can use the device that they feel most comfortable with – the one they already have in their purses and pockets.'),
	('multi_devices_link','Multi-device'),
	('multi_devices_title','Wisembly works everywhere…and we mean everywhere.'),
	('qna_desc1','<p>Your audience can share questions and reactions, whether anonymously or otherwise, via their cell phones without interrupting you.</p> <p>We took part in all kinds of events before we designed Wisembly.</p>'),
	('qna_desc2','<p>Just like us you must be tired of those who are too shy to ask questions and those who love the sound of their own voices and won’t shut up!</p>\r\n<p>With Wisembly everyone gets to have their say and you select the best questions.</p>'),
	('qna_link','Collect questions'),
	('qna_title','Give your audience the chance to speak'),
	('question_answers_desc','Of course, you won’t have time to answer all of the audience’s questions during the seminar.  Why not answer questions directly on Wisembly?  As they come in or straight after the event, you can continue your dialog with your audience and provide satisfactory answers.'),
	('question_answers_link','Answers to questions'),
	('question_answers_title','Continue the dialog with your audience'),
	('votes_desc','Do you usually hand out vote boxes to your participants?  Do you have to take care of the rental, the deposit, and their distribution? Go digital and let your participants use the devices they’ve already got in their pockets and purses.  It’s simpler, it’s cheaper, and it’s definitely more modern!'),
	('votes_link','Votes'),
	('votes_title','Forget about vote boxes!');
INSERT INTO `features_meeting` (`key`, `value`)
VALUES
	('decision_taking_desc1','<p>Meetings are the perfect place to make shared decisions.</p> <p>No need to keep going round and round the table without reaching a clear decision. You can organize a vote on Wisembly with a single click.</p>'),
	('decision_taking_desc2','<p>The result is immediate.</p> <p>You can keep track of every decision taken in all your meetings.</p>'),
	('decision_taking_link','Decision making'),
	('decision_taking_title','Make real decisions during your meetings'),
	('documents_desc1','No need to juggle all your different PowerPoint presentations and documents to email to participants.'),
	('documents_desc2','It’s all there on your Wisembly interface and you decide in one click what to screen and what to make available to everyone. Your content is 100% secure. Only approved users can access it.'),
	('documents_link','Document sharing'),
	('documents_title','All the materials for your meeting all together in one place'),
	('feedback_survey_desc1','Why not get the most out of your participants by gathering feedback at the end of your meeting?'),
	('feedback_survey_desc2','With Wisembly you can get their opinions on how the meeting went, the subjects covered, and the decisions taken – fast.'),
	('feedback_survey_desc3','You can identify unresolved issues with a few clicks and improve future meetings.'),
	('feedback_survey_link','Feedback survey'),
	('feedback_survey_title','Make your meetings better and better'),
	('meeting_confcalls_desc','Wisembly now helps hundreds of companies to prepare, facilitate, and monitor their meetings and conference calls.'),
	('meeting_confcalls_title','Give your meetings a boost'),
	('meeting_poll_choice_1','What every manager must know about Wisembly'),
	('meeting_poll_choice_2','To the Manager who Hates Meetings'),
	('meeting_poll_choice_3','Confidential for innovative Managers'),
	('meeting_poll_title','Which subject line for the next newsletter?'),
	('meeting_stream_item_1_author','Tristan D. via Twitter'),
	('meeting_stream_item_1_text','How to avoid the meeting mania?'),
	('meeting_stream_item_2_author','Mathieu G. via Mobile'),
	('meeting_stream_item_2_text','Is it true that managers spend a third of their time in meetings? '),
	('meeting_stream_item_3_author','Baptiste C. via Web'),
	('meeting_stream_item_3_text','What is your advice to improve the quality of our meetings?'),
	('meeting_stream_item_4_author','Rémy G. via email'),
	('meeting_stream_item_4_text','The meeting mania reveals a communication problem'),
	('meeting_stream_item_5_author','Gabriel M. via SMS'),
	('meeting_stream_item_5_text','How to involve everyone without running out of time?'),
	('multi_devices_desc_1','<p>Why waste time on endless explanations at the start of your meetings?</p>'),
	('multi_devices_desc_2','<p>Your participants connect with the device of their choice (phone, Smartphone, tablet, or PC). No need to create an account, no software to download.</p>'),
	('multi_devices_desc_3','<p>Access is immediate and intuitive.</p>'),
	('multi_devices_link','Multi-device'),
	('multi_devices_title','Your participants are already equipped'),
	('questions_and_ideas_desc1','<p>Every participant can react or ask a question on the subject in hand at any point without interrupting the presentation.</p> <p>You don’t miss any bright ideas.</p>'),
	('questions_and_ideas_desc2','<p>Participants feel involved and that their ideas are heard during your meetings.</p> <p>Everyone can follow the meeting and remain involved.</p>'),
	('questions_and_ideas_link','Collect ideas'),
	('questions_and_ideas_title','Your participants are involved and stay focused'),
	('schedule_link','Schedule'),
	('schedule_meetings_desc','<p>Prepare the agenda for your meeting according to the subjects to be handled.</p> <p>Share it with everyone who will participate in the meeting.</p> <p>Keep track of ideas, suggestions, and decisions taken on each subject with ease.</p>'),
	('schedule_meetings_title','Organize your meetings point by point'),
	('statistics_desc_1','<p>Wisembly remembers everything that happens during your meeting.</p>'),
	('statistics_desc_2','<p>Find the subjects discussed, documents shared, questions raised, and decisions made - fast.</p>'),
	('statistics_desc_3','<p>Wisembly becomes the record of your meetings.</p>'),
	('statistics_link','Statistics and reports'),
	('statistics_title','Keep track of all your meetings');
